}
```

## Properties

- isOpen?: `boolean` **_--- default: true_**
- onClose: () => `void`
- socialConfig: `SocialConfig[]`

# DpConfirmationDialog

## Example

```jsx
export default function Example() {
  const [isOpenDialog, setOpenDialog] = useState(false)

  return (
    <DpConfirmationDialog
      isOpen={isOpenDialog}
      onClose={() => setOpenDialog(false)}
      onCancel={() => setOpenDialog(false)}
      onConfirm={async () => setOpenDialog(false)}
    ></DpConfirmationDialog>
  )
}
```

## Properties

- className?: `string`
- isOpen: `boolean`
- onClose: () => `void`
- title?: `string` // Header content
- content?: `string | ReactNode` // Main content
- confirmLabel?: `string` // Confirm button label
- onConfirm?: () => `void` // Confirm button action
- cancelLabel?: `string` // Cancel button label
- onCancel?: () => `void` // Cancel button action

# DpLoaderProvider

## Example

```jsx
// example.tsx
import { DpLoaderProvider } from '@js-monorepo/loader'

export function Example() {
  return (
    <DpLoaderProvider>
      <Component></Component>
    </DpLoaderProvider>
  )
}
```

```jsx
// component.tsx
import { useLoader } from '@js-monorepo/loader'

export function Component() {
  const { state, setLoaderState } = useLoader()

  return <button onClick={() => setLoaderState({ show: true, message: 'Loading' })}>Trigger loading</button>
}
```

# DpNotificationProvider

## Example

```jsx
// example.tsx
import { DpNotificationProvider } from '@js-monorepo/notification'

export function Example() {
  return (
    <DpNotificationProvider>
      <Component></Component>
    </DpNotificationProvider>
  )
}
```

```jsx
// component.tsx
import { useNotifications } from '@js-monorepo/notification'

export function Component() {
  const { addNotification } = useNotifications()
  return (
    <button
      onClick={() =>
        addNotification({
          message: 'Something went wrong',
          description: 'Please try again later...',
          type: 'error',
          duration: 4000,
        })
      }
    >
      Submit
    </button>
  )
}
```

> addNotification: (notification: NotificationType) => void

NotificationType

- id?: `number`
- type: `'success' | 'error' | 'spinner'`
- message: `string`
- description?: `string`
- duration?: `number`

# DpCheckoutDialog

## Example

```jsx
import { DpCheckoutDialog } from '@js-monorepo/payment'
import { checkoutSessionClient } from '@js-monorepo/utils'

export function Example() {
  const [isOpenCheckoutDialog, setOpenCheckoutDialog] = useState(false)

  return (
    <DpCheckoutDialog
      stripePublishableKey={process.env.NEXT_PUBLIC_STRIPE_PUBLISHABLE_KEY ?? ''}
      isOpen={isOpenCheckoutDialog}
      onClose={() => setOpenCheckoutDialog(false)}
      checkOutPromise={() =>
        checkoutSessionClient({
          username: 'username',
          url: '/api/checkout_sessions',
          customSubmitMessage: 'Thank you for your support',
          isDonate: true,
          price: 500,
        })
      }
    />
  )
}
```

